sys_variable_value.value:
(function execute(inputs, outputs) {

  	var startTime = new GlideDateTime(inputs.startTime);
  	var endTime = new GlideDateTime(inputs.endTime);
  	var usrTZOffset = startTime.getTZOffset();

  		startTime.subtract(usrTZOffset);
		var newStartTime = startTime;

  		endTime.subtract(usrTZOffset);
		var newEndTime = endTime;


	outputs.start_time_utc = newStartTime.toString();
  	outputs.end_time_utc = newEndTime.toString();




})(inputs, outputs);

